export const Comment = ({ name, text, deleteComment, index, id,setCollection }) => {

    function deleteComment(id, ind) {
        setCollection(collection => {
          const updateCollection = [...collection];
          updateCollection[id].comments = updateCollection[id].comments.filter((obj, index) => index !== ind);
          
          console.log(id, ind)
          return updateCollection;
        });
    }
    
    return (
        <li key={index} className='title-user-comment'>
                <h2>{name}</h2>
                <p>{text}</p>
                <button onClick={()=> deleteComment(id,index)} className='title-user-comment-button'>х</button>
        </li>
    )
}
