import React from 'react';
import { Rating } from './Rating';
import { Comment } from './Comment';

export const Film = ({ id, collection,onChangeRating,setCollection }) => {
    const [writeComment, setWriteComment] = React.useState(false);
    const [value, setValue] = React.useState('');
    const [filmComments, setFilmComments] = React.useState([]);
    
    function addComment() {
        if (writeComment && value) {
            setFilmComments((prevComments) => [...prevComments, { name: "user", text: value }]);
            setCollection(collection=>collection[id].comm)
            setValue('');
        };
        setWriteComment(!writeComment);
    }

    function deleteComment(ind) {
        const updateComments = filmComments.filter((el, index) => index !== ind)
        setFilmComments(updateComments)
    }

    return (collection.map((obj, index) => {
        if (index === id) {
            return (
                <div className='title'>
                    <div className='title-film-info'>
                        <img className='title-img' src={obj.photo}></img>
                        <div className='title-info'>
                            <h1 className='title-name'>{obj.name}</h1>
                            <h4 className='title-year'><span>Year:</span> {obj.year}</h4>
                            <Rating
                            collection={collection}
                            onChangeRating={onChangeRating}
                            id={id}
                            />
                            <p><span>Description:</span> {obj.description}</p>
                            <p><span>Director:</span> {obj.director}</p>
                            <p><span>Actors:</span> {obj.actors}</p>
                            <a href={obj.link}><span className='title-link'>Watch</span></a>
                            <ul className='title-comments'>
                                Comments:
                                {filmComments &&
                                    filmComments.map((obj, index) => {
                                        return <Comment
                                            index={index}
                                            deleteComment={deleteComment}
                                            {...obj}
                                        />
                                    })
                                }
                            </ul>
                            {writeComment &&
                                <textarea value={value} onChange ={(e)=>setValue(e.target.value)} placeholder='text' className='title-textarea'></textarea>
                            }
                            <button onClick={addComment} className='title-comment-button'>{writeComment ? 'publish a review' : 'write a review'}</button>
                        </div>
                    </div>
                </div>
            )
        }
    })
    ) 
}
