import React from 'react';
import {Link} from 'react-router-dom'

export const Collection = ({ name, photo, year, description,rating, link}) => {

    const sliceDesc = description.split(' ').slice(0, 20).join(' ') + '...';

    return (
            <Link to="https://www.ilovepdf.com/ru/" target="_blank" rel="noopener noreferrer">
                <li className='collection'>
                    <h1>{name}</h1>
                    <img src={photo} alt="photo"/>
                    <div className='container-info'>
                        <h4>{year}</h4>
                        <p>rating:{rating}</p>
                    </div>
                    <p>{sliceDesc}</p>
                </li>
            </Link>
    )
}
