import './index.css';
import React from 'react';
import { Collection } from './Collection';

function App() {
  const [collection, setCollection] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(true);

  React.useEffect(() =>{
    fetch('https://65573e73bd4bcef8b6124ef7.mockapi.io/films')
      .then(res => res.json())
      .then(json => {
        setCollection(json)
      }).catch(err => {
        console.warn('err')
      }).finally(()=>setIsLoading(false))
  }, [collection,isLoading]);

  return (
    <div className='App'>
      <div className='header'>
        <h1>Collection films</h1>
        <input placeholder='Search name...'></input>
      </div>
      <div className='content'>
        {isLoading ?
          (<h1>Loading...</h1>)
          :
          (collection.map((obj, index) => {
            return <Collection
                    key={index}
                  />
          }))
        }
      </div>
    </div>
  );
}

export default App;
