        <li onClick={() => {setIsFilm(true); setId(index)}}>
                <a href='#' rel="noopener noreferrer" className='collection'>
                    <h1>{name}</h1>
                    <img src={photo} alt="photo"/>
                    <div className='container-info'>
                        <h4>{year}</h4>
                        <p>rating:{rating}</p>
                    </div>
                    <p>{sliceDesc}</p>
                </a>
            </li>
    )
}
