import React from "react";

export const Rating = ({id}) => {
    const [rating, setRating] = React.useState(0);

    function changeRating() {
        setRating()
    }
    return <div className='rating' data-total-value={rating}>
                <div onClick={()=>setRating(5)} className='rating__item'>★</div>
                <div onClick={()=>setRating(4)} className='rating__item'>★</div>
                <div onClick={()=>setRating(3)} className='rating__item'>★</div>
                <div onClick={()=>setRating(2)} className='rating__item'>★</div>
                <div onClick={()=>setRating(1)} className='rating__item'>★</div>
            </div>
}
