import './index.css';
import React from 'react';
import { Collection } from './Components/Collection';
import { Film } from './Components/Film';
import { years, country, rating }  from './Components/options/Options';
import Select from 'react-select';

function App() {
  const [collection, setCollection] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(true);
  const [searchValue, setSearchValue] = React.useState('');
  const [isFilm, setIsFilm] = React.useState(false);
  const [id, setId] = React.useState(null);

  React.useEffect(() => {
    if (localStorage.getItem('films')) {
      setCollection(JSON.parse(localStorage.getItem('films')))
      setIsLoading(false)
    } else {
      fetch('./data.json')
      .then(res => res.json())
      .then(json => {
        setCollection(json)
        localStorage.setItem('films', JSON.stringify(json))
      }).catch(err => {
        console.warn('err')
      }).finally(()=>setIsLoading(false))
    }
  }, []);

  function handleRatingChange(id, newRating) {
    setCollection(collection => {
      const updateCollection = [...collection];
      updateCollection[id].rating = newRating;

      saveToLocal(updateCollection);
      return updateCollection;
    });
  }

  function handleCommentsAdd(id,comment) {
    setCollection(collection => {
      const updateCollection = [...collection];
      updateCollection[id].comments = [...updateCollection[id].comments, { name: "user", text: comment }];

      saveToLocal(updateCollection);
      return updateCollection;
    });
  }

  function deleteComment(id, ind) { 
    setCollection(collection.map((el, index) => {
      if (index === id) {
        el.comments = el.comments.filter((_, i) => i !== ind);
        return el
      }
      return el
    }));
    saveToLocal(collection)
  }

  function saveToLocal(res) {
    localStorage.setItem('films', JSON.stringify(res));
  }

  function reloadMainPage() {
    window.location.reload()
  }

// localStorage.clear()
  return (
    <div className='App'>
          <div className='header'>
          <h1 className='header__title' onClick={reloadMainPage}>Movie collection</h1>
        <div className='w-134'>
          <Select
            placeholder='Choice year...'
            options={years}
          />
          <Select
            placeholder='Choice country...'
            options={country}
          />
          <Select
            placeholder='Choice rating...'
            options={rating}
          />
        </div>
            <input
              value={searchValue}
              onChange={(e) => setSearchValue(e.target.value)}
              placeholder='Search name...'
            >
            </input>
      </div>
      {isFilm ? (
        <Film
          deleteComment={deleteComment}
          key={id}
          addComment={handleCommentsAdd}
          onChangeRating={handleRatingChange}
          id={id}
          collection={collection}
        />
      ) : (<ul className='content'>
        {isLoading ? (
          <h1>Loading...</h1>
        ) : (collection
          .filter(obj => obj.name.toLowerCase().includes(searchValue.toLowerCase().trim()))
          .map((obj, index) => (
            <Collection
              key={obj.id}
              saveToLocal={saveToLocal}
              setIsFilm={setIsFilm}
              {...obj}
              setId={setId}
              index={index}
            />
          ))
        )}
      </ul>)}
    </div>
  );
}

export default App;
