import './index.css';
import React from 'react';
import { Collection } from './Collection';
import { Film } from './Film';

function App() {
  const [collection, setCollection] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(true);
  const [searchValue, setSearchValue] = React.useState('');
  const [isFilm, setIsFilm] = React.useState(false);
  const [id,setId] = React.useState(null);

  React.useEffect(() =>{
    fetch('https://65573e73bd4bcef8b6124ef7.mockapi.io/films')
      .then(res => res.json())
      .then(json => {
        setCollection(json)
      }).catch(err => {
        console.warn('err')
      }).finally(()=>setIsLoading(false))
  }, [collection,isLoading]);

  return (
    <div className='App'>
      {isFilm ? (<Film

      />) :
        (<>
          <div className='header'>
            <h1>Collection films</h1>
            <input
              value={searchValue}
              onChange={(e) => setSearchValue(e.target.value)}
              placeholder='Search name...'
            >
            </input>
          </div>
          <ul className='content'>
            {isLoading ?
              (<h1>Loading...</h1>)
              :
              (collection.filter(obj => {
                return obj.name.toLowerCase().includes(searchValue.toLowerCase().trim())
              })
                .map((obj, index) => {
                  return <Collection
                    onClick={() => {setIsFilm(true); setId(index)}}
                    setIsFilm={setIsFilm}
                    key={index}
                    name={obj.name}
                    photo={obj.photo}
                    year={obj.year}
                    rating={obj.rating}
                    description={obj.description}
                  />
                }))
            }
          </ul>
        </>
      )}
    </div>
  );
}

export default App;
